import { CART_ADD_ITEM } from '../constants/cartConstants';
function cartReducer(state = { cartItems: [] }, action){
    switch(action.type){
        case CART_ADD_ITEM:
            const item = action.payload;
            const puppy = state.cartItems.find(x => x.puppy === item.puppy);
            if (puppy) {
                return {
                    cartItems:
                    state.cartItems.map(x => x.puppy === puppy.puppy ? item: x)
                };
            }

            return { cartItems: [...state.cartItems, item ] };
            default:
                return state
        }
}
export { cartReducer };
