import React from 'react';
import axios from 'axios';
{/*import {Form, FormGroup, Input, Label, Button } from 'reactstrap';*/}
class Contact extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName:'',
      phone:'',
      email: '',
      message: ''
    }
  }

  handleSubmit(e){
    e.preventDefault();
    axios({
      method: "POST", 
      url:"http://localhost:3002/send", 
      data:  this.state
    }).then((response)=>{
      if (response.data.status === 'success') {
        alert("Thank You for your time and interest! \n Home Raised Cairns sent your message successfully."); 
        this.resetForm()
      } else if (response.data.status === 'fail') {
        alert("Message failed to send.")
      }
    })
  }

  resetForm(){
    this.setState({firstName: "", lastName:"", phone:"", email: "", message: ""})
  }

render(){
  return (
    <div className="PageContainer">
    <div className="Page">
    <h1> Contact </h1>
    <div className="Intro">
      <div className="email">

    <a className="left" href="mailto:homeraisedcairns@gmail.com"> Email:  HomeRaisedCairns@gmail.com </a>

      <a className="right" href="tel:5418199410">  Phone: (541) 819 9410 </a>
      <br />
    </div>
    </div>



  <div className="PageBody">

  <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
          <div className="form-group">
              <label htmlFor="firstName">First Name:</label>
              <input type="text" className="form-control" id="firstName" value={this.state.name} onChange={this.onFirstNameChange.bind(this)} />
          </div>
          <div className="form-group">
              <label htmlFor="lastName">Last Name: </label>
              <input type="text" className="form-control" id="lastName" value={this.state.name} onChange={this.onLastNameChange.bind(this)} />
          </div>
          <div className="form-group">
              <label htmlFor="phone">Phone Number: </label>
              <input type="text" className="form-control" id="phone" value={this.state.name} onChange={this.onPhoneChange.bind(this)} />
          </div>
          <div className="form-group">
              <label htmlFor="exampleInputEmail1">Email address</label>
              <input type="email" className="form-control" id="email" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)} />
          </div>
          <div className="form-group">
              <label htmlFor="message">Message</label>
              <textarea className="form-control" rows="5" id="message" value={this.state.message} onChange={this.onMessageChange.bind(this)} />
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>
        </div>
        </div>
        </div>

      );
  }
  onFirstNameChange(event) {
    this.setState({name: event.target.value})
  }
  onLastNameChange(event) {
    this.setState({email: event.target.value})
  }  
  onPhoneChange(event) {
    this.setState({email: event.target.value})
  }  
  onEmailChange(event) {
    this.setState({email: event.target.value})
  }

  onMessageChange(event) {
    this.setState({message: event.target.value})
  }
}

export default Contact;
