import React,{ useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { detailsPuppy } from '../actions/puppyActions';

function PuppyPage(props) {

    const puppyDetails = useSelector(state => state.puppyDetails);
    const { puppy, loading, error } = puppyDetails;
    const dispatch = useDispatch();

  useEffect(() => {
    dispatch(detailsPuppy(props.match.params.id));
      return () => {
          //
      }
  }, []);
  const handleAddtoReview = () => {
      props.history.push("/cart" + props.match.params.id)
  }

    return <div className="puppyDetailsStyle">
    <div className="back-to-result">
      <Link to="/">Back to result</Link>
    </div>
    {loading ? <div>Loading...</div> :
      error ? <div>{error} </div> :
        (
    <div className="details">
    <div className="details-image">
    <img src={puppy.image} alt="puppy"></img>
    </div>
    <div className="details-info">
        <ul>
            <li>
                <h4>Hello, My name is {puppy.name}

                </h4>
            </li>
            <li>
            Birth Day: {puppy.dob}
            </li>
            <li>
            Color: {puppy.color}
            </li>
            <li>
            Registration: {puppy.reg}
            </li>
            <li>
            Show Potiential? : {puppy.potiential}
            </li>
            <li>
              Gender:  {puppy.gender}
            </li>
            <li>
                Status: {puppy.status}
            </li>
            <li>
                Price: ${puppy.price}
            </li>
            {puppy.status == "Available" ? 
            <button onClick={handleAddtoReview} className="button">
                Purchase Options
            </button>
            :
            <div>Sold to {puppy.client} </div>
        }
        </ul>
    </div>
    </div>
    )
    }
    </div>
}
export default PuppyPage;
