import { REVIEW_ADD_ITEM } from '../constants/reviewConstants';
function reviewReducer(state = { reviewItems: [] }, action){
    switch(action.type){
        case REVIEW_ADD_ITEM:
            const item = action.payload;
            const puppy = state.reviewItems.find(x => x.puppy === item.puppy);
            if (puppy) {
                return {
                    reviewItems:
                    state.reviewItems.map(x => x.puppy === puppy.puppy ? item: x)
                };
            }

            return { reviewItems: [...state.reviewItems, item ] };
            default:
                return state
        }
}
export { reviewReducer };
