import React,{ useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { detailsPuppy } from '../actions/puppyActions';
function PuppyPage(props) {

    const puppyDetails = useSelector(state => state.puppyDetails);
    const { puppy, loading, error } = puppyDetails;
    const dispatch = useDispatch();

  useEffect(() => {
    dispatch(detailsPuppy(props.match.params.id));
      return () => {
          //
      }
  }, []);
  const handleAddtoReview = () => {
      props.history.push("/cart" + props.match.params.id)
  }

    return <div className="PageContainer">

      <div className="Page">
        <h1> Avaliable Puppy </h1>

          <div id="Title">Puppy Details</div>


    <div className="puppyDetailsStyle">
      <Link to="/">
    <div className="back-to-result">
      Back to result
    </div>
    </Link>
    {loading ? <div>Loading...</div> :
      error ? <div>{error} </div> :
        (

    <div className="details">
    <div className="details-image">
    <img src={puppy.image} alt="puppy"></img>
    </div>
    <div className="Intro">{puppy.name} is a wonderful {puppy.discription}.</div>
    <div className="details-info">
        <ul>
            <li>
                <div className="li">Hello, My name is {puppy.name}

                </div>
            </li>
            <li>
            I was born: {puppy.dob}
            </li>
            <li>
            My color right now is: {puppy.color}
            </li>
            <li>
            I come with an option of: {puppy.reg}
            </li>
            <li>
            Show Potiential? : {puppy.potiential}
            </li>
            <li>
            I am a:  {puppy.gender}
            </li>
            <li>
            Status: {puppy.status}
            </li>
            {puppy.status == "Available" ? 
            <button onClick={handleAddtoReview} className="button">
                Purchase Options
            </button>
            :
            <div className="li"> Sorry, Found My Family </div>
        }
        </ul>
    </div>
    </div>
    )
    }
    </div>
    </div>
    </div>
}
export default PuppyPage;
