import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import { useSelector, useDispatch } from 'react-redux';
import { listPuppies } from '../actions/puppyActions';
import { Card, CardDeck, CardGroup } from 'react-bootstrap';

function HomePage(props) {
    const puppyList = useSelector(state => state.puppyList);
    const { puppies, loading, error } = puppyList;
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(listPuppies());
        return () => {

        };
    }, [])
    return loading? <div>loading...</div>:
    error? <div>{error}</div>:
    <div className="puppiesContainer">
        <h1>Puppies</h1>
        <h4>Unfortunitly no puppies are avaible right now, but you can reserve the next one avaible today!</h4>
        <br /> 
        <br />
        <h4> Past Puppies for sale
        </h4>
    <CardGroup className="PuppiesGroup">

    { 
          puppies.map(puppy => 
      <Card> 
          <Link to={'/puppy/' + puppy._id}>
            <Card.Img variatn="top" src={puppy.image} />
               <Card.Title>
                   Name: {puppy.name}
               </Card.Title>
                <div className="puppy-gender">{puppy.gender}</div>
                {/* <div className="puppy-price"> ${puppy.price}</div> */}
            </Link>
        </Card>)
    }

    </CardGroup>

    </div>
}
export default HomePage;
