import React, { useState, useCallback } from "react";
import { render } from "react-dom";
import Gallery from "react-photo-gallery";
import '../App.css';
import Carousel, { Modal, ModalGateway } from "react-images";
import { photos } from "./photos";

const GalleryPage = () => {

      const [currentImage, setCurrentImage] = useState(0);
      const [viewerIsOpen, setViewerIsOpen] = useState(false);

      const openLightbox = useCallback((event, { photo, index }) => {
        setCurrentImage(index);
        setViewerIsOpen(true);
      }, []);

      const closeLightbox = () => {
        setCurrentImage(0);
        setViewerIsOpen(false);
      };
      return (
        <div className="GalleryContainer">
            <div className="Page">
          <h1>Home Raised Cairns</h1>
          <div className="Intro">
            <div id="Title"> Gallery  </div>
            This gallery is some of the places in the country my Cairns have been.
            </div>
<div className="PageBody">
  <div id="Title"> Photo Gallery</div>
      <Gallery photos={photos} onClick={openLightbox} />
      <ModalGateway>
        {viewerIsOpen ? (
          <Modal onClose={closeLightbox}>
            <Carousel
              currentIndex={currentImage}
              views={photos.map(x => ({
                ...x,
                srcset: x.srcSet,
                caption: x.title
              }))}
            />
          </Modal>
        ) : null}
      </ModalGateway>
    </div>

    <div className="PageSum">
    <p>
        Thank in your interest in Home Raised Cairns.
        <br />
        HRC: SiteMap 
        <br />
    </p>
    </div>
    </div>
    </div>
      ); 
    }


  export default GalleryPage;
