import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import { useSelector, useDispatch } from 'react-redux';
import { listPuppies } from '../actions/puppyActions';
import { Card, CardDeck, CardGroup } from 'react-bootstrap';

function HomePage(props) {
    const puppyList = useSelector(state => state.puppyList);
    const { puppies, loading, error } = puppyList;
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(listPuppies());
        return () => {

        };
    }, [])
    return loading? <div>loading...</div>:
    error? <div>{error}</div>:
    <div className="puppiesContainer">
        <h1>Puppies</h1>
        <h4>Unfortunately no puppies are available right now, but you can reserve one today! 
            Find out more about reserving your next best friend here. (link to reservations) 
            There are currently people on a reservation list for the next litter. 
            Please feel free to browse puppies from home raised cairns listed below. </h4>
        <br /> 
        <br />
        <div className="PageContainer">
<div className="Page">
        <p id="Title"> Previously sold Litter </p>
    <CardGroup className="PuppiesGroup">

    { 
          puppies.map(puppy => 
      <Card> 
          <Link to={'/puppy/' + puppy._id}>
            <Card.Img variatn="top" src={puppy.image} />
               <Card.Title>
                   Name: {puppy.name}
               </Card.Title>
                <div className="puppy-gender">{puppy.gender}</div>
                {/* <div className="puppy-price"> ${puppy.price}</div> */}
            </Link>
        </Card>)
    }

    </CardGroup>
    </div>
    </div>
    </div>
}
export default HomePage;
