import React, { useEffect } from 'react';
import { addToDeposit, removeFromDeposit } from '../actions/depositActions';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
function DepositPage(props) {

  const deposit = useSelector(state => state.deposit);

  const { depositPuppies } = deposit;

  const puppyId = props.match.params.id;

  const dispatch = useDispatch();
  const removeFromDepositHandler = (puppyId) => {
    dispatch(removeFromDeposit(puppyId));
  }
  useEffect(() => {
    if (puppyId) {
      dispatch(addToDeposit(puppyId));
    }
  }, []);

  const checkoutHandler = () => {
    props.history.push("/signin?redirect=shipping");
  }

  return <div className="cart">
    <div className="cart-list">
      <ul className="cart-list-container">
        <li>
          <h3>
            Shopping Cart
          </h3>
          <div>
            Price
          </div>
        </li>
        {
          Puppies.length === 0 ?
            <div>
              Cart is empty
          </div>
            :
            depositPuppies.map(puppies =>
              <li>
                <div className="cart-image">
                  <img src={puppies.image} alt="product" />
                </div>
                <div className="cart-name">
                  <div>
                    <Link to={"/puppy/" + puppies.puppy}>
                      {puppies.name}
                    </Link>

                  </div>
                  <div>

                    <button type="button" className="button" onClick={() => removeFromDepositHandler(item.puppy)} >
                      Delete
                    </button>
                  </div>
                </div>
                <div className="cart-price">
                  ${puppy.price}
                </div>
              </li>
            )
        }
      </ul>

    </div>
    <div className="cart-action">
      <h3>
        Subtotal ( {depositItems.reduce((a, c) => a + c.qty, 0)} items)
        :
         $ {puppy.price}
      </h3>
      <button onClick={checkoutHandler} className="button primary full-width" disabled={depositPuppies.length === 0}>
        Proceed to Checkout
      </button>

    </div>

  </div>
}

export default DepositPage;
