import { createStore, combineReducers, compose, applyMiddleware } from 'redux';
import { puppyListReducer, puppyDetailsReducer } from '../reducers/puppyReducers';
import thunk from 'redux-thunk';
import Cookie from 'js-cookie';
import { userSigninReducer, userRegisterReducer, userUpdateReducer } from './reducers/userReducers';
import { depositReducer } from '../reducers/depositReducers.js';

const userInfo = Cookie.getJSON("userInfo") || null; 

const initialState= { deposit:{}, userSignin: { userInfo } };
const reducer = combineReducers({
    puppyList: puppyListReducer,
    puppyDetails: puppyDetailsReducer,
    deposit: depositReducer,
    userSignin: userSigninReducer,
    userRegister: userRegisterReducer
})

const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
const store = createStore(reducer, initialState, composeEnhancer(applyMiddleware(thunk)));
export default store;
