import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import { useSelector, useDispatch } from 'react-redux';
import { listPuppies } from '../actions/puppyActions';
import { Jumbotron, Card, Row, Col, Container, CardDeck, CardGroup } from 'react-bootstrap';

const HomePage = (props) => {
    const puppyList = useSelector(state => state.puppyList);
    const { puppies, loading, error } = puppyList;
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(listPuppies());
        return () => {
        };
    }, [])
    return loading? <div>loading...</div>:
    error? <div>{error}</div>:
    <div className="PageContainer">

<div className="Page">
<h1>Puppies</h1>
    <div className="Intro">
    <Jumbotron fluid>
        <Container>
          <h1>Fluid jumbotron</h1>
          <p>
            This is a modified jumbotron that occupies the entire horizontal space of
            its parent.
          </p>
        </Container>
      </Jumbotron>
      </div>
{/*         
        <p>Unfortunately no puppies are available right now, but you can reserve one today! 
            Find out more about reserving your next best friend here. (link to reservations) 
            There are currently people on a reservation list for the next litter. 
            Please feel free to browse puppies from home raised cairns listed below. </p>
        <br /> 
        <br /> */}

    <div className="PageBody">
        <p id="Title"> Previously sold Litter </p>
    <div className="PuppyHomeCard"> 
    { 
          puppies.map(puppy =>  
      <Card className="PuppyHomeCard"> 
          <Link to={'/puppy/' + puppy._id}>
              <Row>
                  <Col>
            <Card.Img variatn="top" src={puppy.image} />
                  </Col>
                  <Col>
               <Card.Title>
                   Name: {puppy.name}
               </Card.Title>
                <div className="puppy-gender">{puppy.gender}</div>
                <div className="puppy-price"> ${puppy.price}</div>
                </Col>
                </Row>
            </Link>
        </Card>)

}
    </div>
    </div>
    <div className="PageSum">
    <div id="Title"> Example Summary</div>
    <p>
        Thank in your interest in Home Raised Cairns.
        <br />
        HRC: SiteMap 
        <br />
    </p>

    </div>
    </div>
    </div>
}
export default HomePage;
