import React from 'react';
import axios from 'axios';
import {Form, FormGroup, Input, Label, Button } from 'reactstrap';
class Contact extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      message: ''
    }
  }

  handleSubmit(e){
    e.preventDefault();
    axios({
      method: "POST", 
      url:"http://localhost:3002/send", 
      data:  this.state
    }).then((response)=>{
      if (response.data.status === 'success') {
        alert("Thank You for your time and interest! \n Krotzer Software Developmnet sent your message successfully."); 
        this.resetForm()
      } else if (response.data.status === 'fail') {
        alert("Message failed to send.")
      }
    })
  }

  resetForm(){
    this.setState({name: "", email: "", message: ""})
  }

render(){
  return (
    <div className="PageContainer">
    <div className="Page">
    <h1> Contact </h1>
    <div className="Intro">
      <div className="email">
    {/*Email: >*/}
    Email: <a href="mailto:homeraisedcairns@gmail.com"> HomeRaisedCairns@gmail.com </a>
    <br />
    Phone:  <a href="tel:5418199410"> (541) 819 9410 </a>
    </div>
    </div>



  <div className="PageBody">

  <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
          <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" className="form-control" id="name" value={this.state.name} onChange={this.onNameChange.bind(this)} />
          </div>
          <div className="form-group">
              <label htmlFor="exampleInputEmail1">Email address</label>
              <input type="email" className="form-control" id="email" aria-describedby="emailHelp" value={this.state.email} onChange={this.onEmailChange.bind(this)} />
          </div>
          <div className="form-group">
              <label htmlFor="message">Message</label>
              <textarea className="form-control" rows="5" id="message" value={this.state.message} onChange={this.onMessageChange.bind(this)} />
          </div>
          <button type="submit" className="btn btn-primary">Submit</button>
        </form>
